var changecolor = context.get('changecolor')||1;
var flipflop = context.get('flipflop')||0;
//var newMsg = {payload: msg.payload};
//var newMsgColor = {payload: msg.payload};
//var newMsgToggle = {payload: msg.payload};
// toggle switch function
if (msg.payload == 2) {
    if (flipflop === 0) {
        flipflop = 1;
        msg = {payload: "OFF"};
    } else if (flipflop === 1) {
        flipflop = 0;
        msg = {payload: "ON"};
    }
    context.set('flipflop',flipflop);
    msg.isDevice = "flipflop";
    msg.flipflop = flipflop;
    return msg;
} else if (msg.payload == 1) {
    if (changecolor === 1) {
        changecolor = 2;
        msg = {payload: "WHITE"};
    } else if (changecolor === 2) {
        changecolor = 3;
        msg = {payload: "RED"};
    } else if (changecolor === 3) {
        changecolor = 4;
        msg = {payload: "GREEN"};
    } else if (changecolor === 4) {
        changecolor = 1;
        msg = {payload: "BLUE"};
    }
    context.set('changecolor',changecolor);
    msg.isDevice = "changecolor";
    msg.changecolor = changecolor;
    return msg;
} else msg = {payload: "other scene"};
return msg;

